import React, { useEffect, useState } from "react";
import { useState as useReactState } from "react";
import "./App.css";
import Splash from "./Splash";

function App() {
    // 404 state
    const [notFound, setNotFound] = useReactState(false);

    // Listen for hash changes (simulate routing)
    useEffect(() => {
      const onHashChange = () => {
        if (window.location.hash && window.location.hash !== "#/") {
          setNotFound(true);
        } else {
          setNotFound(false);
        }
      };
      window.addEventListener("hashchange", onHashChange);
      onHashChange();
      return () => window.removeEventListener("hashchange", onHashChange);
    }, []);
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState({
    name: "",
    address: "",
    phone: "",
  });
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [showSplash, setShowSplash] = useState(true);
  // Login/signup removed

  useEffect(() => {
    fetch("http://localhost:5000/api/menu")
      .then((res) => res.json())
      .then(setMenu);
  }, []);

  const addToCart = (item) => setCart([...cart, item]);
  const handleInputChange = (e) =>
    setCustomer({ ...customer, [e.target.name]: e.target.value });

  const placeOrder = async () => {
    if (
      !customer.name ||
      !customer.address ||
      !customer.phone ||
      cart.length === 0
    )
      return;
    const res = await fetch("http://localhost:5000/api/order", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items: cart, customer }),
    });
    const data = await res.json();
    setOrderPlaced(true);
    setCart([]);
    // WhatsApp order message
    const orderMsg = `Order from ${customer.name} (%2B91${customer.phone}):%0A${cart.map((i) => i.name + " - ₹" + i.price).join("%0A")}%0AAddress: ${customer.address}`;
    window.open(`https://wa.me/91YOUR_ADMIN_NUMBER?text=${orderMsg}`);
  };

  if (showSplash) return <Splash onFinish={() => setShowSplash(false)} />;
  if (notFound) {
    return (
      <div className="radhe-container" style={{ textAlign: "center", paddingTop: 60 }}>
        <div style={{ fontSize: 48, fontWeight: 700, color: '#c0392b' }}>404.</div>
        <div style={{ fontSize: 24, margin: '16px 0' }}>This page<br />doesn't exist.</div>
        <button className="place-order-btn" style={{ maxWidth: 200 }} onClick={() => { window.location.hash = "/"; setNotFound(false); }}>
          Return home
        </button>
      </div>
    );
  }
  return (
    <div className="radhe-container">
      <div className="radhe-title">Radhe Foods</div>
      <div className="radhe-section">
        <h2>Menu</h2>
        <ul className="menu-list">
          {menu.map((item) => (
            <li className="menu-item" key={item.id}>
              <span>
                {item.name} - ₹{item.price}
              </span>
              <button onClick={() => addToCart(item)}>Add</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="radhe-section">
        <h2>Cart</h2>
        <ul className="cart-list">
          {cart.map((item, idx) => (
            <li key={idx}>
              {item.name} - ₹{item.price}
            </li>
          ))}
        </ul>
      </div>
      <div className="radhe-section">
        <h2>Customer Details</h2>
        <div className="input-row">
          <input
            name="name"
            placeholder="Name"
            value={customer.name}
            onChange={handleInputChange}
          />
          <input
            name="address"
            placeholder="Address"
            value={customer.address}
            onChange={handleInputChange}
          />
          <input
            name="phone"
            placeholder="Phone"
            value={customer.phone}
            onChange={handleInputChange}
          />
        </div>
        <button className="place-order-btn" onClick={placeOrder}>
          Place Order (COD)
        </button>
        {orderPlaced && (
          <div className="order-success">
            Order placed! You will be contacted soon.
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
