import React, { useEffect, useState } from "react";
import "./App.css";
import Splash from "./Splash";

function App() {
  const [menu, setMenu] = useState([]);
  const [cart, setCart] = useState([]);
  const [customer, setCustomer] = useState({
    name: "",
    address: "",
    phone: "",
  });
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [showSplash, setShowSplash] = useState(true);
  const [authMode, setAuthMode] = useState("login");
  const [auth, setAuth] = useState({ username: "", password: "" });
  const [user, setUser] = useState(null);
  const [authError, setAuthError] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/menu")
      .then((res) => res.json())
      .then(setMenu);
  }, []);

  const addToCart = (item) => setCart([...cart, item]);
  const handleInputChange = (e) =>
    setCustomer({ ...customer, [e.target.name]: e.target.value });
  const handleAuthInput = (e) =>
    setAuth({ ...auth, [e.target.name]: e.target.value });

  const handleAuth = async (e) => {
    e.preventDefault();
    setAuthError("");
    const endpoint = authMode === "login" ? "/api/login" : "/api/signup";
    const res = await fetch(`http://localhost:5000${endpoint}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(auth),
    });
    const data = await res.json();
    if (res.ok) {
      setUser(data.user || { username: auth.username });
    } else {
      setAuthError(data.error || "Authentication failed");
    }
  };

  const placeOrder = async () => {
    if (
      !customer.name ||
      !customer.address ||
      !customer.phone ||
      cart.length === 0
    )
      return;
    const res = await fetch("http://localhost:5000/api/order", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items: cart, customer }),
    });
    const data = await res.json();
    setOrderPlaced(true);
    setCart([]);
    // WhatsApp order message
    const orderMsg = `Order from ${customer.name} (%2B91${customer.phone}):%0A${cart.map((i) => i.name + " - ₹" + i.price).join("%0A")}%0AAddress: ${customer.address}`;
    window.open(`https://wa.me/91YOUR_ADMIN_NUMBER?text=${orderMsg}`);
  };

  if (showSplash) return <Splash onFinish={() => setShowSplash(false)} />;
  if (!user) {
    return (
      <div className="radhe-container">
        <div className="radhe-title">Radhe Foods</div>
        <div className="radhe-section" style={{ textAlign: "center" }}>
          <h2>{authMode === "login" ? "Login" : "Sign Up"}</h2>
          <form onSubmit={handleAuth} style={{ maxWidth: 320, margin: "auto" }}>
            <input
              name="username"
              placeholder="Username"
              value={auth.username}
              onChange={handleAuthInput}
              className="input-row"
              style={{ marginBottom: 10 }}
            />
            <input
              name="password"
              type="password"
              placeholder="Password"
              value={auth.password}
              onChange={handleAuthInput}
              className="input-row"
              style={{ marginBottom: 10 }}
            />
            <button className="place-order-btn" type="submit">
              {authMode === "login" ? "Login" : "Sign Up"}
            </button>
            {authError && (
              <div style={{ color: "#c0392b", marginTop: 8 }}>{authError}</div>
            )}
          </form>
          <div style={{ marginTop: 12 }}>
            {authMode === "login" ? (
              <span>
                Don't have an account?{" "}
                <button
                  style={{
                    color: "#27ae60",
                    background: "none",
                    border: 0,
                    cursor: "pointer",
                  }}
                  onClick={() => setAuthMode("signup")}
                >
                  Sign Up
                </button>
              </span>
            ) : (
              <span>
                Already have an account?{" "}
                <button
                  style={{
                    color: "#27ae60",
                    background: "none",
                    border: 0,
                    cursor: "pointer",
                  }}
                  onClick={() => setAuthMode("login")}
                >
                  Login
                </button>
              </span>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
